mapper.select:
SELECT id, owner_user_id, plan_id, schedule_title, meet_at, status, created_at, updated_at
FROM drinking_schedule
WHERE id = #{id}

SELECT id, owner_user_id, plan_id, schedule_title, meet_at, status, created_at, updated_at
FROM drinking_schedule
WHERE owner_user_id = #{userId}
  AND meet_at BETWEEN #{startDate} AND #{endDate}
ORDER BY meet_at ASC

mapper.update:
UPDATE drinking_schedule
schedule_title = #{scheduleTitle}, meet_at = #{meetAt}, status = #{status}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, owner_user_id, plan_id, schedule_title, meet_at, status, created_at, updated_at
FROM drinking_schedule
WHERE owner_user_id = #{ownerUserId}
ORDER BY meet_at DESC
LIMIT #{limit} OFFSET #{offset}
